import React, { useRef } from "react";
import { Helmet } from "react-helmet";
import {
  MainContainer,
  DashboardContainer,
  ControlsContainer,
  ChartsContainer,
  LogoContainer
} from "./styles";
import { ControlContainer } from "./components";
import { useSelector } from "react-redux";
import { selectFilteredCoordinates } from "../../store/furnitureBankReducer/selectors";
import { MapContainer } from "./styles/styledContainers";
import styled from "styled-components";
import ReactMapGL from 'react-map-gl';
import SimpleDot from "../../components/IconComponents/SimpleDot";
import {heatMapStyles, HEATMAP_SOURCE_ID} from './components/Map/styles';

const Dashboard = () => {
  const filteredCoordinates = useSelector(selectFilteredCoordinates);
  const mapRef = useRef()
  const viewport = {
    width: "100%",
    height: "100%",
    latitude: 43.65107,
    longitude: -79.347015,
    zoom: 9
  };


  const makeGeoJSON = (data) => {
    if (!data ) return []
    return {
      type: 'FeatureCollection',
      features: data.map(feature => {
        return {
          "type": "Feature",
          "properties": {
            "id": feature.postal_code,
            "name": feature.name
          },
          "geometry": {
            "type": "Point",
            "coordinates": [ feature.latitude, feature.longitude]
          }
        }
      })
    }
  };

  const handleMapLoaded = () => {
    const myGeoJSONData = makeGeoJSON(filteredCoordinates);
    console.log(mapRef.current.getMap())

    mapRef.current.getMap().addSource(HEATMAP_SOURCE_ID, {type: 'geojson', myGeoJSONData});
    mapRef.current.getMap().addLayer(heatMapStyles);
  };

  return (
    <>
      <Helmet>
        <title>Furniture Bank 2019</title>
      </Helmet>
      <MainContainer>
        <DashboardContainer>
          <ControlsContainer>
            <LogoContainer>
              <img
                src="/static/logos/furnitureBank.svg"
                alt="furniture bank icon"
              />
            </LogoContainer>
            <ControlContainer />
          </ControlsContainer>
          <ChartsContainer>
            <MapContainer>
              <ReactMapGL
                ref={mapRef}
                mapboxApiAccessToken="pk.eyJ1Ijoic3plYW5kciIsImEiOiJjajlpeWxnNHUzbWNiMzNvcnF1ZGUzbG5rIn0.fk33YGLOHSZtT_CMEWoIag"
                {...viewport}
                mapStyle="mapbox://styles/szeandr/cje3v8qwedxub2st94yulcma7"
                onLoad={handleMapLoaded}
              >
              {/* <Source type="geojson" data={myGeoJSONData}>
                <Layer {...heatmapLayer} />
              </Source> */}
              {/* <HeatmapOverlay locations={cities} {...viewport}/> */}
                {/* {coordinatesData.map(coor => (
                  <Marker latitude={coor.latitude} longitude={coor.longitude}>
                    <SimpleDot widht={5} height={5} />
                  </Marker>
                ))} */}
              </ReactMapGL>
            </MapContainer>
          </ChartsContainer>
        </DashboardContainer>
      </MainContainer>
    </>
  );
};
export default Dashboard;
