import React from 'react';
import styled, { css } from 'styled-components'
import { colorGrey } from '../../styles';

const Container = styled.div`
  position: fixed;
  background-color: ${props => props.background || colorGrey};
  color: ${props => props.color || '#fff'};
  ${props => css`
    top: ${props.top};
    right: ${props.right};
    bottom: ${props.bottom};
    left: ${props.left};
    width: ${props.width}px;
    height: ${props.height}px;
  `}
  border-radius: ${props => props.round && '100%'};
  transform: translate(${props => props.corner && props.translate});
  cursor: pointer;
`

const IconContainer = styled.div`
  position: relative;
  height: 100%;
  width: 100%;
  border: 1px solid black;
`

const Icon = styled.div`
  position: absolute;
  height: 20px;
  width: 20px;
  background-color: #fff;
  bottom: 20%;
  left: 250%;
`

const FixedInfoButton = ({
  top, right, bottom, left, background, color, height, width, round, corner, translate
}) => {
  const dims = { top, right, bottom, left, height, width, corner, translate }
  const styles = { background, color, round }
  return (
    <Container
      {...dims}
      {...styles}
    >
      <IconContainer>
        <Icon/>
      </IconContainer>
    </Container>
  )
}

export default FixedInfoButton
