import React, { useRef, useEffect, useState } from "react";
import styled from "styled-components";
import { useSvgResize } from "../../../hooks";
import { testBorder } from "../../../styles";
import { select } from "d3-selection";
import { colorGreen, colorGreyLight, colorGreyDark } from "../styles";
import chroma from 'chroma-js'

const ChartWrapper = styled.div`
  position: relative;
  height: 80%;
  width: 90%;

  border: 1px solid ${colorGreyLight};
  background-color: ${chroma(colorGreyDark).brighten()};

  border-radius: 20px;
  overflow: hidden;

  svg {
    position: absolute;
    width: 100%;
    height: 100%;
  }
`;

const PercentageChart = ({ 
  data,
  margin = {
    top:1, right: 1, bottom: 1, left: 1
  }

}) => {
  const svgRef = useRef();
  const storedValues = useRef();
  const [init, setInit] = useState(false);
  let initVis

  const dims = useSvgResize(svgRef);

  useEffect(() => {
    if (dims.width && dims.height && data && !init) {
      initVis();
      setInit(true);
    }
  }, [data, dims.height, dims.width, init, initVis]);

  useEffect(() => {
    //console.log('dims', dims)
  }, [dims]);

  useEffect(() => {
    //console.log('data', data)
  }, [data]);

  initVis = () => {
    const svg = select(svgRef.current);
    svg
      .append("g")
      .attr("class", "chart-area")
      .attr('transform', `translate(${margin.left}, ${margin.top})`)
    const chartArea = select(".chart-area");
    const chartWidth = dims.width - margin.right - margin.left
    const chartHeight = dims.height - margin.top - margin.bottom

    storedValues.current = {
      chartArea,
      chartWidth,
      chartHeight
    };
  };

  const updateData = () => {};

  const resetChart = () => {};

  const updateDims = () => {};

  return (
    <ChartWrapper>
      <svg ref={svgRef} />
    </ChartWrapper>
  );
};

export default PercentageChart;
