import styled from "styled-components";
import { testBorder } from "../../styles/sharedStyles";
import { createBreakPoints } from '../../styles/mediaQueries'

export const colorGreen = '#3db692'
export const colorRedDark = '#b02b2c'
export const colorRed = '#ec1c24'
export const colorGreyLight = 'lightgray'
export const colorGreyDark = '#333'

const breakPoints = {
  largest: 1400,
  large: 1000,
  medium: 600,
}

const media = createBreakPoints(breakPoints)

////////////////* CONTAINERS ////////////////////////

export const MainContainer = styled.div`
  margin: 0;
  padding: 0;
  width: 100vw;
  height: 100vh;

  display: flex;
  justify-content: center;
  align-items: center;
`;

export const DashboardContainer = styled.div`
  width: 90%;
  height: 900px;
  max-width: 1440px;
  margin: 5rem 0;

  display: grid;
  grid-template-columns: 200px 1fr;
  grid-template-areas: "control dashboard";

  ${media.largest`
    background-color: red;
  `}

  ${testBorder};
`

export const ControlsContainer = styled.div`
  display: flex;

  grid-area: control

  ${testBorder}
`


////////////////* OTHER ////////////////////////
