import React, { useRef, useEffect, useState, useCallback } from "react";
import styled from "styled-components";
import { useSvgResize, usePrevious } from "../../../hooks";
import { select } from "d3-selection";
import { colorGreen, colorGreyDark } from "../styles";
import { easeCubicInOut } from "d3-ease";
import "d3-transition";
import { createUpdateSvg } from "../../../utils";

const ChartWrapper = styled.div`
  position: relative;
  height: 70%;
  width: 90%;

  border: 1px solid ${colorGreyDark};

  border-radius: 20px;
  overflow: hidden;

  svg {
    position: absolute;

    rect {
      fill: ${colorGreen};
    }
  }
`;

const PercentageChart = ({
  data,
  fullCount,
  updateDuration = 1000,
  resetDelay = 500
}) => {
  const divRef = useRef();
  const storedValues = useRef();
  const prevData = usePrevious(data);
  const prevCount = usePrevious(fullCount);
  const [init, setInit] = useState(false);
  let initVis, resetChart;

  const dims = useSvgResize(divRef);

  useEffect(() => {
    if (dims.width && dims.height && data && !init) {
      initVis();
      setInit(true);
    }
  }, [data, dims.height, dims.width, init, initVis]);

  useEffect(() => {
    //console.log('dims', dims)
  }, [dims]);

  useEffect(() => {
    if (init && prevData !== data) {
      if (prevCount !== fullCount) {
        resetChart();
        updateRect({ delay: resetDelay });
      } else {
        updateRect();
      }
    }
  }, [
    data,
    init,
    prevData,
    fullCount,
    prevCount,
    resetChart,
    updateDuration,
    resetDelay,
    updateRect
  ]);

  initVis = () => {
    const area = select(divRef.current);
    const { chartArea } = createUpdateSvg({
      area,
      dims: { ...dims, height: dims.height },
      append: true
    });

    chartArea
      .append("rect")
      .attr("x", 0)
      .attr("y", 0)
      .attr("height", dims.height)
      .attr("width", 0);

    storedValues.current = {
      area,
      chartArea
    };

    updateRect();
  };

  resetChart = () => {
    const { chartArea } = storedValues.current;
    chartArea
      .select("rect")
      .transition("toFull")
      .duration(resetDelay)
      .ease(easeCubicInOut)
      .attr("width", dims.width)
      .transition("toZero")
      .delay(resetDelay)
      .duration(0)
      .attr("width", 0);
  };

  const updateDims = () => {};

  const updateRect = useCallback((duration = updateDuration, delay = 0) => {
    const { chartArea } = storedValues.current;
    console.log(duration);
    chartArea
      .select("rect")
      .transition("update")
      .duration(duration)
      .ease(easeCubicInOut)
      .delay(delay)
      .attr("width", dims.width * (data - fullCount));
  }, [data, dims.width, fullCount, updateDuration]);

  return <ChartWrapper ref={divRef} />;
};

export default PercentageChart;
