import { select } from "d3-selection";
import { colorGreen, colorGreyLight } from "../styles";
import { scaleLinear } from 'd3-scale'

const ChartWrapper = styled.div`
  position: relative;
  height: 80%;
  width: 90%;

  border: 1px solid ${colorGreyLight};

  border-radius: 20px;
  overflow: hidden;

  svg {
    position: absolute;
    width: 100%;
    height: 100%;

    rect {
      fill: ${colorGreen};
    }
  }
`;

