  const dims = useSvgResize(svgRef);

  useEffect(() => {
    if (dims.width && dims.height && data && !init) {
      initVis();
      setInit(true);
    }
  }, [data, dims.height, dims.width, init, initVis]);

  useEffect(() => {
    //console.log('dims', dims)
  }, [dims]);

  useEffect(() => {
    //console.log('data', data)
  }, [data]);

  initVis = () => {
    const svg = select(svgRef.current);
    svg
      .append("g")
      .attr("class", "chart-area")
      .attr('transform', `translate(${margin.left}, ${margin.top})`)
    const chartArea = select(".chart-area");
    const chartWidth = dims.width - margin.right - margin.left
    const chartHeight = dims.height - margin.top - margin.bottom
    const xScale = scaleLinear()

    storedValues.current = {
      chartArea,
      chartWidth,
      chartHeight
    };
  };

