import React from 'react';
import styled, { css } from 'styled-components'
import { paddingXS, fontWeightXS, fontSizeM, paddingS, fontWeightXL, fontSizeL, fontSizeS, fontSizeXS, fontWeightL } from '../../styles/sharedStyles';
import { colorGreyDark } from '../../containers/FurnitureBank2019/styles';

const Container = styled.div`
    position: relative;

    display: flex;
    align-items: center;

    width: calc(100% - 4rem);
    height: ${props => props.height || 30}px;
    padding: ${paddingXS};
    margin: ${paddingS};
    padding-left: 3rem;
    font-weight: ${props => props.fontWeight || fontWeightXS};
    font-size: ${fontSizeM};
    border: 1px solid ${props => props.borderColor || colorGreyDark};
    border-radius: 4px;

    background: ${colorGreyDark};
    color: #fff;

    transition: .5s all;

    &:hover div:after, 
    &:hover div:before {
      visibility: visible;
    }
`

const QuantityContainer = styled.div`
  position: absolute;
  width: 15px;
  left: 1rem;
  top: 50%;
  transform: translateY(-50%);

  display: flex;
  justify-content: center;

  font-weight: ${fontWeightXL};
`

const ControlElement = styled.div`
    position: absolute;
    top: ${props => props.top}px;
    left: 1.4rem;
    cursor: pointer;
    font-weight: ${fontWeightL};
    ${'' /* visibility: hidden; */}
    color: #fff;
`

const ItemChip = ({
  style
}) => {

  return (
    <Container
      {...style}
    >
      <ControlElement
          top={-2}
        >+</ControlElement>
      <QuantityContainer>
        20
      </QuantityContainer>
      Air Conditioner
    </Container>
  )
}

export default ItemChip
