import React, { useState } from "react";
import Autosuggest from "react-autosuggest";
import match from "autosuggest-highlight/match";
import parse from "autosuggest-highlight/parse";
import TextField from "@material-ui/core/TextField";
import Paper from "@material-ui/core/Paper";
import MenuItem from "@material-ui/core/MenuItem";
import { fontWeightS, fontWeightL } from "../../styles/sharedStyles";
import { Grid } from "@material-ui/core";
import { useStyles, AddIcon } from "./styles";


const escapeRegexCharacters = str => str.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
const getSuggestionValue = suggestion => console.log(suggestion);

//`${suggestion.first} ${suggestion.last}`

const getSuggestions = (data, key, value) => {
  const escapedValue = escapeRegexCharacters(value.trim());

  if (escapedValue === "") {
    return [];
  }

  const regex = new RegExp("\\b" + escapedValue, "i");

  return data.filter(el => regex.test(getSuggestionValue(el[key])));
}


const renderSuggestion = (suggestion, { query }) => {
  console.log(suggestion);

  // const suggestionText = `${suggestion.first} ${suggestion.last}`;
  // const matches = match(suggestionText, query);
  // const parts = parse(suggestionText, matches);

  // return (
  //   <span className={"suggestion-content " + suggestion.twitter}>
  //     <span className="name">
  //       {parts.map((part, index) => {
  //         const className = part.highlight ? "highlight" : null;
  //         return (
  //           <span className={className} key={index}>
  //             {part.text}
  //           </span>
  //         );
  //       })}
  //     </span>
  //   </span>
  // );
}

const AutoComplete = ({
  data, key
}) => {
  const [value, setValue] = useState("");
  const [suggestions, setSuggestions] = useState([]);

  const handleOnChange = (_, { newValue }) => setValue(newValue);
  const onSuggestionsFetchRequested = ({ value }) => setSuggestions(getSuggestions(data, key, value));
  const onSuggestionsClearRequested = () => setSuggestions([]);

  const inputProps = {
    placeholder: "Add new item",
    value,
    onChange: handleOnChange
  };
  return (
    <Autosuggest
      suggestions={suggestions}
      onSuggestionsFetchRequested={onSuggestionsFetchRequested}
      onSuggestionsClearRequested={onSuggestionsClearRequested}
      getSuggestionValue={getSuggestionValue}
      renderSuggestion={renderSuggestion}
      inputProps={inputProps}
    />
  );
};

export default AutoComplete;
