import React, { useState } from "react";
import deburr from "lodash/deburr";
import Autosuggest from "react-autosuggest";
import match from "autosuggest-highlight/match";
import parse from "autosuggest-highlight/parse";
import TextField from "@material-ui/core/TextField";
import Paper from "@material-ui/core/Paper";
import MenuItem from "@material-ui/core/MenuItem";
import AccountCircle from "@material-ui/icons/AccountCircle";
import { makeStyles } from "@material-ui/core/styles";
import { fontWeightS, fontWeightL } from "../../styles/sharedStyles";
import { Grid } from "@material-ui/core";

function renderInputComponent(inputProps) {
  const { classes, inputRef = () => {}, ref, ...other } = inputProps;
  const useStyles = makeStyles(theme => ({
    container: {
      display: 'flex',
      flexWrap: 'wrap',
    },
    textField: {
      marginLeft: theme.spacing(1),
      marginRight: theme.spacing(1),
    },
    dense: {
      marginTop: theme.spacing(2),
    },
    menu: {
      width: 200,
    },
  }));
  const styles = useStyles();
  return (
    <Grid 
      container 
      spacing={1} 
      alignItems="flex-end"
    >
      <Grid item>
        <AccountCircle />
      </Grid>
      <Grid item>
        <TextField
          id="outlined-search"
          type="search"
          className={styles.textField}
          margin="normal"
          variant="outlined"
          fullWidth
          InputProps={{
            inputRef: node => {
              ref(node);
              inputRef(node);
            },
            classes: {
              input: classes.input
            }
          }}
          {...other}
        />
      </Grid>
    </Grid>
  );
}

function getSuggestions(data, itemKey, value) {
  const inputValue = deburr(value.trim()).toLowerCase();
  const inputLength = inputValue.length;
  let count = 0;

  return inputLength === 0
    ? []
    : data.filter(item => {
        const keep =
          count < 5 &&
          item[itemKey].slice(0, inputLength).toLowerCase() === inputValue;

        if (keep) {
          count += 1;
        }

        return keep;
      });
}

const useStyles = makeStyles(theme => ({
  root: {
    height: 250,
    flexGrow: 1
  },
  container: {
    position: "relative"
  },
  suggestionsContainerOpen: {
    position: "absolute",
    zIndex: 1,
    marginTop: theme.spacing(1),
    left: 0,
    right: 0
  },
  suggestion: {
    display: "block"
  },
  suggestionsList: {
    margin: 0,
    padding: 0,
    listStyleType: "none"
  },
  divider: {
    height: theme.spacing(2)
  }
}));

function AutoComplete({ data, itemKey }) {
  const classes = useStyles();

  const [single, setSingle] = React.useState("");

  const [stateSuggestions, setSuggestions] = useState([]);

  const getSuggestionValue = item => item[itemKey];

  const handleSuggestionsFetchRequested = ({ value }) =>
    setSuggestions(getSuggestions(data, itemKey, value));

  const handleSuggestionsClearRequested = () => setSuggestions([]);

  const handleChange = (_, { newValue }) => setSingle(newValue);

  const renderSuggestion = (data, { query, isHighlighted }) => {
    const matches = match(data[itemKey], query);
    const parts = parse(data[itemKey], matches);

    return (
      <MenuItem selected={isHighlighted} component="div">
        <div>
          {parts.map(part => (
            <span
              key={part.text}
              style={{ fontWeight: part.highlight ? fontWeightL : fontWeightS }}
            >
              {part.text}
            </span>
          ))}
        </div>
      </MenuItem>
    );
  };

  const autosuggestProps = {
    renderInputComponent,
    suggestions: stateSuggestions,
    onSuggestionsFetchRequested: handleSuggestionsFetchRequested,
    onSuggestionsClearRequested: handleSuggestionsClearRequested,
    getSuggestionValue,
    renderSuggestion
  };

  return (
    <div className={classes.root}>
      <Autosuggest
        {...autosuggestProps}
        inputProps={{
          classes,
          id: "react-autosuggest-simple",
          placeholder: "Add a new item",
          value: single,
          onChange: handleChange
        }}
        theme={{
          container: classes.container,
          suggestionsContainerOpen: classes.suggestionsContainerOpen,
          suggestionsList: classes.suggestionsList,
          suggestion: classes.suggestion
        }}
        renderSuggestionsContainer={options => (
          <Paper {...options.containerProps} square>
            {options.children}
          </Paper>
        )}
      />
    </div>
  );
}

export default AutoComplete;
