import React from 'react';
import styled, { css } from 'styled-components'
import { paddingXS, fontWeightXS, fontSizeM, paddingM } from '../../styles/sharedStyles';

const Container = styled.div`
    display: flex;
    align-items: center;

    width: calc(100% - 2.4rem);
    height: ${props => props.height || 30}px;
    padding: ${paddingXS};
    margin: ${paddingM};
    padding-left: ${props => !props.searched && 3}rem;
    font-weight: ${props => props.fontWeight || fontWeightXS};
    font-size: ${fontSizeM};
    border: 1px solid ${props => props.borderColor || '#aaa'};
    border-radius: 4px;

    transition: .5s all;

    &:focus {
      outline: none;
    }
`

const ItemChip = ({
  style
}) => {

  return (
    <Container
      {...style}
    >
      ItemChip
    </Container>
  )
}

export default ItemChip
