import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet";
import { Button } from "@material-ui/core";
import SendIcon from "@material-ui/icons/Send";
import { makeStyles } from "@material-ui/core/styles";

import {
  MainContainer,
  DashboardContainer,
  ControlsContainer,
  ChartsContainer,
  LogoContainer,
  MainTitle,
  SocChartsContainer,
  EnvChartsContainer,
  LandFillContainer,
  EmissionContainer,
  ReqContainer,
  ChartPaper,
  colorRed,
  ChartTitle,
  colorGreen
} from "./styles";
import {
  ValueContainer,
  MapContainer,
  ControlContainer,
  FamiliesContainer
} from "./containers";
import { Message } from "./components";
import {
  fontSizeM,
  FlexContainer,
  TextSpan,
  fontWeightL,
  fontSizeXL
} from "../../styles";
import { LandFillSubContainer } from "./styles/styledContainers";
import { useSelector } from "react-redux";
import {
  selectAllSelectedItems,
  selectAllSelectedPrice,
  selectAllSelectedVolume,
  selectAllSelectedWeight
} from "../../store/furnitureBankReducer/selectors";
import { ConvertedNumber } from "../../components";

const useStyles = makeStyles(theme => ({
  button: {
    background: colorRed,
    color: "white",
    fontSize: fontSizeM,
    boxShadow: "none",
    "&:hover": {
      background: colorRed,
      color: "white"
    }
  }
}));

const AVG_MONTHLY_WASTE = 130;
const AVG_WEEKLY_WASTE = 30;

const Dashboard = () => {
  const classes = useStyles();
  const [ monthCount, setMonthCount ] = useState(0)
  const [ weekCount, setWeekCount ] = useState()

  const items = useSelector(selectAllSelectedItems);
  const price = useSelector(selectAllSelectedPrice);
  const weight = useSelector(selectAllSelectedWeight);
  const volume = useSelector(selectAllSelectedVolume);

  const getMonth = () => Math.floor(weight/AVG_MONTHLY_WASTE)

  useEffect(() => {
    if(getMonth() !== monthCount){
      setMonthCount(getMonth())
    }
  }, [getMonth, monthCount, weight])  

  useEffect(() => {
    const getWeek = () => Math.floor((weight - (monthCount * AVG_MONTHLY_WASTE))/AVG_WEEKLY_WASTE)
    if(getWeek() !== weekCount){
      setWeekCount(getWeek())
    }
  }, [monthCount, weekCount, weight])  

  return (
    <>
      <Helmet>
        <title>Furniture Bank 2019</title>
      </Helmet>
      <MainContainer>
        <DashboardContainer>
          <ControlsContainer>
            <LogoContainer>
              <img
                src="/static/logos/furnitureBank.svg"
                alt="furniture bank icon"
              />
            </LogoContainer>
            <ControlContainer />
          </ControlsContainer>
          <ChartsContainer>
            <SocChartsContainer>
              <MainTitle gridArea="title">
                What is the estimated social impact of your donation?
              </MainTitle>
              <MapContainer />
              <FamiliesContainer />
              <ValueContainer />
            </SocChartsContainer>
            <EnvChartsContainer>
              <MainTitle gridArea="title">
                What is the estimated environmental impact of your donation?
              </MainTitle>
              <ChartPaper gridArea="landfill">
                <LandFillContainer>
                  <ChartTitle>
                    What is the estimated amount of waste saved?
                  </ChartTitle>
                  <Message />
                  {
                    weight &&
                      <LandFillSubContainer gridArea="weight">
                        <FlexContainer area="num">
                          <ConvertedNumber
                            data={weight}
                            size={fontSizeXL}
                            weight={fontWeightL}
                            color={colorGreen}
                            animate
                            smallSuffix
                            suffixSize={fontSizeM}
                            suffixLineHeight={1.5}
                            customSuffix="lbs"
                          />
                        </FlexContainer>
                        <FlexContainer
                          area="icon"
                          align="flex-end"
                        >
                            { 
                              !!monthCount &&
                              Array.from(Array(Math.floor(monthCount)), (_, i) => i + 1).map(
                                el => 
                                <img
                                  key={el}
                                  className="garbage-month"
                                  src="/static/icons/furnitureBank/garbage.svg"
                                  alt="garbage month icon"
                                />
                              )
                            }
                            {
                              !!getWeek(weight) && 
                              Array.from(Array(Math.floor(getWeek(weight))), (_, i) => i + 1).map(
                                el => 
                                <img
                                  key={el}
                                  className="garbage-week"
                                  src="/static/icons/furnitureBank/garbage.svg"
                                  alt="garbage week icon"
                                />
                              )
                          }
                        </FlexContainer>
                        <FlexContainer area="text">
                          <TextSpan>
                            2 months and 2 weeks of waste in an avg. Canadian household
                          </TextSpan>
                        </FlexContainer>
                      </LandFillSubContainer>
                    }
                  }
                  <LandFillSubContainer gridArea="volume">
                    <FlexContainer area="num">Number</FlexContainer>
                    <FlexContainer area="icon">Icons</FlexContainer>
                    <FlexContainer area="text">
                      <TextSpan>
                        Equivalent to 20% of an avg. cargo container
                      </TextSpan>
                    </FlexContainer>
                  </LandFillSubContainer>
                </LandFillContainer>
              </ChartPaper>
              <EmissionContainer>
                <ChartPaper>
                  <ChartTitle>
                    What is the estimated CO2 emission saved?
                  </ChartTitle>
                  <Message />
                </ChartPaper>
              </EmissionContainer>
            </EnvChartsContainer>
            <ReqContainer>
              <Button
                className={classes.button}
                variant="contained"
                disableRipple
              >
                Request a Pickup
              </Button>
            </ReqContainer>
          </ChartsContainer>
        </DashboardContainer>
      </MainContainer>
    </>
  );
};
export default Dashboard;
