import React, { useRef, useEffect, useState } from "react";
import styled from "styled-components";
import { useSvgResize } from "../../../hooks";
import { testBorder } from "../../../styles";
import { select } from "d3-selection";

const ChartWrapper = styled.div`
  position: relative;
  ${testBorder};
  height: 100%;
  width: 100%;

  svg {
    position: absolute;
    width: 100%;
    height: 100%;
  }
`;

const PercentageChart = ({ 
  data,
  margin = {
    top:1, right: 1, bottom: 1, left: 1
  }

}) => {
  const svgRef = useRef();
  const storedValues = useRef();
  const [init, setInit] = useState(false);

  const dims = useSvgResize(svgRef);

  useEffect(() => {
    if (dims.width && dims.height && data && !init) {
      initVis();
      setInit(true);
    }
  }, [data, dims.height, dims.width, init, initVis]);

  useEffect(() => {
    //console.log('dims', dims)
  }, [dims]);

  useEffect(() => {
    //console.log('data', data)
  }, [data]);

  const initVis = () => {
    const svg = select(svgRef.current);
    svg
      .append("g")
      .attr("class", "chart-area")
      .attr('transform', `translate(${margin.left}, ${margin.top})`)
    const chartArea = select(".chart-area");

    storedValues.current = {
      chartArea
    };
  };

  const updateData = () => {};

  const resetChart = () => {};

  const updateDims = () => {};

  return (
    <ChartWrapper>
      <svg ref={svgRef} />
    </ChartWrapper>
  );
};

export default PercentageChart;
