import styled from "styled-components";
import { testBorder } from "../../styles/sharedStyles";
import { createQueries } from '../../styles/mediaQueries'

export const colorGreen = '#3db692'
export const colorRedDark = '#b02b2c'
export const colorRed = '#ec1c24'
export const colorGreyLight = 'lightgray'
export const colorGreyDark = '#333'

const breakPoints = {
  largest: 1400,
  large: 1000,
  medium: 600,
  // small: 770,
  // smallest: 600,
  // tiny: 450
}



////////////////* CONTAINERS ////////////////////////

export const MainContainer = styled.div`
  margin: 0;
  padding: 0;
  width: 100vw;
  height: 100vh;

  display: flex;
  justify-content: center;
  align-items: center;
`;

export const DashboardContainer = styled.div`
  width: 80%;
  height: 900px;
  min-width: 800px;
  max-width: 1440px;

  margin: 5rem 0;

  ${testBorder};
`

