    console.log(dims.height );
    console.log(!init);
    if (dims.width && dims.height && data && !init) {
      initVis();
      setInit(true);
    }
  }, [data, dims.height, dims.width, init,   initVis]);

  useEffect(() => {
    //console.log('dims', dims)
  }, [dims]);

  useEffect(() => {
    if(init && (prevData !== data)){
      if(data > 1){

      } else {   
        // updateData()
      }
    }
  }, [data, init, prevData, updateData]);

  initVis = () => {
    select(divRef.current).append('svg')
    select('svg')
      .attr('width', dims.width)
      .attr('height', dims.height)
      .append("g")
      .attr("class", "chart-area")
    const chartArea = select(".chart-area");
    chartArea
      .append('rect')
      .attr('x', 0)
      .attr('y', 0)
      .attr('height', dims.height + 1)
      .attr('width', dims.width * data)

    storedValues.current = {
      chartArea
    };
  };

  updateData = () => {
    const { chartArea } = storedValues.current
    chartArea.select('rect')
      .transition()
      .duration(1000)
      .ease(easeCubicInOut)
      .attr('width', dims.width * data)
  };

  const resetChart = () => {
    const { chartArea } = storedValues.current
    chartArea.select('rect')
      .attr('width', 0)
