import React from "react";
import { Helmet } from "react-helmet";
import {
  MainContainer,
  DashboardContainer,
  ControlsContainer,
  ChartsContainer,
  LogoContainer,
  MainTitle,
  ChartTitle,
  colorGreen
} from "./styles";
import { ControlContainer, MapContent } from "./components";
import {
  SocChartsContainer,
  FamilyContainer,
  ValueContainer,
  EnvChartsContainer,
  LandFillContainer,
  EmissionContainer,
  ReqContainer,
  PersonsContainer
} from "./styles/styledContainers";
import styled from "styled-components";
import _ from "lodash";
import { Button } from "@material-ui/core";
import SendIcon from "@material-ui/icons/Send";
import { makeStyles } from "@material-ui/core/styles";
import { fontSizeM } from "../../styles/sharedStyles";
import { ChartPaper } from "./styles/styledElements";
import { useSelector } from "react-redux";
import {
  selectPostalCodeFamilies,
  selectTopGroups,
  selectAdults,
  selectChildren,
  selectAllSelected
} from "../../store/furnitureBankReducer/selectors";

const useStyles = makeStyles(theme => ({
  rightIcon: {
    marginLeft: theme.spacing(1)
  },
  button: {
    background: colorGreen,
    color: "white",
    fontSize: fontSizeM,
    "&:hover": {
      background: colorGreen,
      color: "white"
    }
  }
}));

const Dashboard = () => {
  const classes = useStyles();

  const selected = useSelector(selectAllSelected)
  const adults = useSelector(selectAdults);
  const children = useSelector(selectChildren);
  const adultsArray = Array.from(Array(Math.round(adults || 0)).keys());
  const childrenArray = Array.from(Array(Math.round(children || 0)).keys());

  console.log(selected);
  console.log(adults);

  return (
    <>
      <Helmet>
        <title>Furniture Bank 2019</title>
      </Helmet>
      <MainContainer>
        <DashboardContainer>
          <ControlsContainer>
            <LogoContainer>
              <img
                src="/static/logos/furnitureBank.svg"
                alt="furniture bank icon"
              />
            </LogoContainer>
            <ControlContainer />
          </ControlsContainer>
          <ChartsContainer>
            <SocChartsContainer>
              <MainTitle gridArea="title">
                What is the estimated social impact of your donation?
              </MainTitle>
              <MapContent />
              <ChartPaper gridArea="families">
                <FamilyContainer>
                  <ChartTitle>
                    Who will be likely to receive your donation?
                  </ChartTitle>
                  {
                    selected.length === 0 &&
                    <ChartTitle message>
                        Please start by adding furnitures!
                    </ChartTitle> 
                  }
                  {
                    !adults 
                      &&
                    <ChartTitle message>
                        Sorry, we do not have data for these the {selected.length === 1 ? 'item' : 'items'} you selected!
                    </ChartTitle>
                  }
                  <PersonsContainer>
                    <div>
                      {adultsArray.map(el => (
                        <img
                          key={el}
                          className="adult"
                          src="/static/icons/furnitureBank/person.svg"
                          alt="adult person icon"
                        />
                      ))}
                      {childrenArray.map(el => (
                        <img
                          key={el}
                          className="child"
                          src="/static/icons/furnitureBank/person.svg"
                          alt="child person icon"
                        />
                      ))}
                    </div>
                  </PersonsContainer>
                </FamilyContainer>
              </ChartPaper>
              <ValueContainer>
                <ChartPaper />
              </ValueContainer>
            </SocChartsContainer>
            <EnvChartsContainer>
              <MainTitle gridArea="title">
                What is the estimated environmental impact of your donation?
              </MainTitle>
              <LandFillContainer>
                <ChartPaper />
              </LandFillContainer>
              <EmissionContainer>
                <ChartPaper />
              </EmissionContainer>
            </EnvChartsContainer>
            <ReqContainer>
              <Button
                className={classes.button}
                variant="contained"
                disableRipple
              >
                Request a Pickup
                <SendIcon fontSize="small" className={classes.rightIcon} />
              </Button>
            </ReqContainer>
          </ChartsContainer>
        </DashboardContainer>
      </MainContainer>
    </>
  );
};
export default Dashboard;
