import React from 'react';
import styled, { css } from 'styled-components'

const ModalBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, .4);
  display: ${props => props.visible ? 'block' : 'none'};
  z-index: 1000;
`

const ModalPaper = styled.div`
  position: absolute;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 600px;
  height: 300px;
  z-index: 1100;
  background-color: #fff;

  ${props => props.visible ? css`
    transition: 1s all;
    opacity: 1;
    top: 50%;
    display: block;
  ` : css`
    transition: 0s all;
    opacity: 0;
    top: -50%;
    display: none;
  `}
`

const Modal = ({
  children,
  open,
  handleClick
}) => {

  return (
    <>
      <ModalBackground 
        onClick={handleClick} 
        open={open}
      />
      <ModalPaper
        open={open}
      >
        {children}
      </ModalPaper>
    </>
  )
}

export default Modal
