import React, { useEffect, useRef, useState } from "react";
import { Helmet } from "react-helmet";
import {
  MainContainer,
  DashboardContainer,
  ControlsContainer,
  ChartsContainer,
  LogoContainer,
  ControlContainer,
  MainTitle,
  PanelContainer
} from "./styles";
import { AutoComplete, ItemChip } from "../../components";
import { useSelector, useDispatch } from "react-redux";
// import styled from 'styled-components'

import { addItem, incrementItem, decrementItem } from "../../store/furnitureBankReducer/actions";
import {
  selectAllSelected,
  selectAllUnselected
} from "../../store/furnitureBankReducer/selectors";
// import ReactMapGL, { Marker, Popup } from "react-map-gl";
// import SimpleDot from "../../components/IconComponents/SimpleDot";

const Dashboard = () => {
  const [width, setWidth] = useState(undefined);
  const panelContainerRef = useRef();
  const dispatch = useDispatch();

  useEffect(() => {
    const panelWidth = panelContainerRef.current.clientWidth;
    !width && setWidth(panelWidth);
  }, [panelContainerRef, width]);

  const handleSelected = selected => dispatch(addItem(selected));
  const handleIncrementClick = item => dispatch(incrementItem(item))
  const handleDecrementClick = item => dispatch(decrementItem(item))

  const { 
    selectedItems
  } = useSelector(state => state.furnitureBankReducer)
  const allSelected = useSelector(selectAllSelected);
  const allUnselected = useSelector(selectAllUnselected);

  console.log(allSelected);
  console.log(selectedItems);

  return (
    <>
      <Helmet>
        <title>Furniture Bank 2019</title>
      </Helmet>
      <MainContainer>
        <DashboardContainer>
          <ControlsContainer>
            <LogoContainer>
              <img
                src="/static/logos/furnitureBank.svg"
                alt="furniture bank icon"
              />
            </LogoContainer>
            <ControlContainer>
              <MainTitle gridArea="title">Items to Donate</MainTitle>
              <PanelContainer ref={panelContainerRef}>
                <ItemChip 
                  handleIncrementClick={() => handleIncrementClick('Air Conditioner')}
                  handleDecrementClick={() => handleDecrementClick('Air Conditioner') }
                />
                <AutoComplete
                  data={allUnselected}
                  itemKey="product_name"
                  handleSelected={handleSelected}
                  style={{
                    width
                  }}
                />
              </PanelContainer>
            </ControlContainer>
          </ControlsContainer>
          <ChartsContainer>Charts</ChartsContainer>
        </DashboardContainer>
      </MainContainer>
    </>
  );
};
export default Dashboard;

