import React from "react";
import { Helmet } from "react-helmet";
import { Button } from "@material-ui/core";
import SendIcon from "@material-ui/icons/Send";
import { makeStyles } from "@material-ui/core/styles";

import {
  MainContainer,
  DashboardContainer,
  ControlsContainer,
  ChartsContainer,
  LogoContainer,
  MainTitle,
  SocChartsContainer,
  EnvChartsContainer,
  LandFillContainer,
  EmissionContainer,
  ReqContainer,
  ChartPaper,
  colorRed,
  ChartTitle
} from "./styles";
import {
  ValueContainer,
  MapContainer,
  ControlContainer,
  FamiliesContainer
} from "./containers";
import { Message } from "./components";
import { fontSizeM, FlexContainer, TextSpan } from "../../styles";
import { LandFillSubContainer } from "./styles/styledContainers";
import { useSelector } from "react-redux";
import { selectAllSelectedItems, selectAllSelectedPrice, selectAllSelectedVolume, selectAllSelectedWeight } from "../../store/furnitureBankReducer/selectors";
import { ConvertedNumber } from "../../components";

const useStyles = makeStyles(theme => ({
  button: {
    background: colorRed,
    color: "white",
    fontSize: fontSizeM,
    boxShadow: "none",
    "&:hover": {
      background: colorRed,
      color: "white"
    }
  }
}));

const Dashboard = () => {
  const classes = useStyles();

  const items = useSelector(selectAllSelectedItems)
  const price = useSelector(selectAllSelectedPrice)
  const weight = useSelector(selectAllSelectedWeight)
  const volume = useSelector(selectAllSelectedVolume)

  console.log(items);
  console.log(price);
  console.log(weight);
  console.log(volume);

  return (
    <>
      <Helmet>
        <title>Furniture Bank 2019</title>
      </Helmet>
      <MainContainer>
        <DashboardContainer>
          <ControlsContainer>
            <LogoContainer>
              <img
                src="/static/logos/furnitureBank.svg"
                alt="furniture bank icon"
              />
            </LogoContainer>
            <ControlContainer />
          </ControlsContainer>
          <ChartsContainer>
            <SocChartsContainer>
              <MainTitle gridArea="title">
                What is the estimated social impact of your donation?
              </MainTitle>
              <MapContainer />
              <FamiliesContainer />
              <ValueContainer />
            </SocChartsContainer>
            <EnvChartsContainer>
              <MainTitle gridArea="title">
                What is the estimated environmental impact of your donation?
              </MainTitle>
                <ChartPaper gridArea="landfill">
                  <LandFillContainer>
                      <ChartTitle>What will be the estimated amount of waste saved?</ChartTitle>
                      <Message />
                      <LandFillSubContainer gridArea="weight" >
                          <FlexContainer area="num" >
                              <ConvertedNumber
                                data={weight}
                              />
                          </FlexContainer>
                          <FlexContainer area="icon" >
                              Icons
                          </FlexContainer>
                          <FlexContainer area="text" >
                            <TextSpan>
                              Equivalent to 15.5 days of waste in an avg. Canadian household
                            </TextSpan>
                          </FlexContainer>
                      </LandFillSubContainer>
                      <LandFillSubContainer gridArea="volume" >
                        <FlexContainer area="num" >
                                Number
                            </FlexContainer>
                            <FlexContainer area="icon" >
                                Icons
                            </FlexContainer>
                            <FlexContainer area="text" >
                              <TextSpan>
                                Equivalent to 20% of an avg. cargo container
                              </TextSpan>
                            </FlexContainer>
                        </LandFillSubContainer>
                  </LandFillContainer>
                </ChartPaper>
              <EmissionContainer>
                <ChartPaper>
                  <Message />
                </ChartPaper>
              </EmissionContainer>
            </EnvChartsContainer>
            <ReqContainer>
              <Button
                className={classes.button}
                variant="contained"
                disableRipple
              >
                Request a Pickup
              </Button>
            </ReqContainer>
          </ChartsContainer>
        </DashboardContainer>
      </MainContainer>
    </>
  );
};
export default Dashboard;
