import { paddingM, paddingS, testBorder } from "../../../styles/sharedStyles";
import styled from "styled-components";
import { media, titleHeightM, gridGapM, gridGapS, colorGreen } from "./variables";
// import { border } from "@material-ui/system";

export const MainContainer = styled.div`
  width: 100vw;
`;

export const DashboardContainer = styled.div`
  width: 90%;
  height: 650px;
  max-width: 1440px;

  margin: 0 auto;
  padding-top: 5rem;
  padding-bottom: 5rem;

  display: grid;
  grid-template-columns: 300px 1fr;
  grid-template-areas: "control charts";

  ${'' /* ${testBorder}; */}

  ${media.medium`
    padding-top: 3rem;
    padding-bottom: 3rem;
    height: 850px;
    grid-template-columns: 1fr;
    grid-template-rows: 200px 1fr;
    grid-template-areas:  
      "control"
      "charts";
  `}

  ${media.small`
    width: 92.5%;
    padding-top: 2rem;
    padding-bottom: 2rem;
    grid-template-rows: 300px 1fr;
    height: 1350px;
  `}

  ${media.extraSmall`
    padding-top: 0;
    padding-bottom: 2rem;
    width: 100%;
    height: 1300px;
  `}
`;

export const ControlsContainer = styled.div`
  display: grid;
  grid-area: control;

  grid-template-rows: 100px 1fr;
  grid-template-columns: 1fr;
  grid-template-areas:
    "title"
    "panel";

  ${'' /* ${testBorder}; */}

  padding: ${paddingM};

  ${media.medium`
    grid-template-columns: 250px 1fr;
    grid-template-rows: 1fr;
    grid-template-areas:  "title panel";
    padding: 0;
    grid-column-gap: ${gridGapM};
  `}

  ${media.small`
    grid-template-columns: 1fr;
    grid-template-rows: 120px 1fr;
    grid-template-areas:  
      "title" 
      "panel";
    grid-column-gap: 0;
    grid-row-gap: ${gridGapM};
  `}

  ${media.extraSmall`
    grid-template-columns: 1fr;
    grid-template-rows: 100px 1fr;
    grid-template-areas:  
      "title" 
      "panel";
    grid-column-gap: 0;
    grid-row-gap: ${gridGapM};
  `}
`;

export const LogoContainer = styled.div`
  grid-area: title;
  padding: ${paddingS};

  display: flex;
  align-items: flex-start;
  background: #fff;

  ${testBorder};

  img {
    width: 100%;
  }

  ${media.medium`
    padding: 0;
    align-items: center;
  `}

  ${media.small`
    background: ${colorGreen};
    img {
      width: 80%;
    }
  `}
`;

export const ControlContainer = styled.div`
  grid-area: panel;
  display: grid;

  padding: ${paddingS};

  ${'' /* ${testBorder}; */}

  grid-template-rows: ${titleHeightM} 1fr;
  grid-template-areas:
    "title"
    "panel";

  ${media.medium`
    padding: 0;
    grid-column-gap: ${gridGapM};
  `}
`;

export const PanelContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  height: 350px;
  overflow-y: scroll;

  ${'' /* ${testBorder}; */}

  &::-webkit-scrollbar {
    display: none;
  }
  grid-area: panel;

  ${media.medium`
    height: 150px;
  `}

  ${media.small`
    height: 125px;
  `}

  ${media.extraSmall`
    height: 160px;
  `}
`;

export const ChartsContainer = styled.div`
  display: grid;
  grid-area: charts;
  grid-row-gap: ${gridGapS};
  padding: ${paddingM};

  ${media.medium`
    padding: 0;
  `}

  grid-template-rows: 3fr 2fr ${titleHeightM};
  grid-template-areas:
    "soc"
    "env"
    "req";
`;

export const SocChartsContainer = styled.div`
  display: grid;
  grid-area: soc;
  grid-row-gap: ${gridGapS};
  grid-column-gap: ${gridGapM};

  grid-template-rows: ${titleHeightM} 1fr 100px;
  grid-template-columns: repeat(2, 1fr);
  grid-template-areas:
    "title title"
    "map families"
    "value value";

  ${media.small`
    grid-template-rows: ${titleHeightM} repeat(2, 1fr) 100px;
    grid-template-columns: 1fr;
    grid-template-areas:
      "title"
      "map"
      "families"
      "value"
  `}
`;

export const MapContainer = styled.div`
  grid-area: map;
  height: 100%;
  width: 100%;
`;

export const FamilyContainer = styled.div`
  grid-area: families;
  height: 100%;
  width: 100%; 
`;

export const ValueContainer = styled.div`
  grid-area: value;
  height: 100%;
  width: 100%; 
`

export const EnvChartsContainer = styled.div`
  display: grid;
  grid-area: env;
  grid-row-gap: ${gridGapS};
  grid-column-gap: ${gridGapM};

  grid-template-rows: ${titleHeightM} 1fr;
  grid-template-columns: repeat(2, 1fr);
  grid-template-areas:
    "title title"
    "landfill emission";

  ${media.small`
    grid-template-rows: ${titleHeightM} repeat(2, 1fr);
    grid-template-columns: 1fr;
    grid-template-areas:
      "title"
      "landfill"
      "emission";
  `}
`;

export const LandFillContainer = styled.div`
  grid-area: landfill;
  height: 100%;
  width: 100%;
`;

export const EmissionContainer = styled.div`
  grid-area: emission;
  height: 100%;
  width: 100%;
`;

export const ReqContainer = styled.div`
  grid-area: req;
  display: flex;
  margin-top: 1.6rem;
  justify-content: space-between;
  align-items: center;
`;
