  const dims = useSvgResize(divRef)
  const prevDims = usePrevious(dims)

  useEffect(() => {
    console.log(_.isEqual(prevData, data));
  }, [data, prevData])

  useEffect(() => {

  }, [dims])


  return <ChartWrapper ref={divRef} />;
};

export default BarChart;
