import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet";

import {
  MainContainer,
  DashboardContainer,
  ControlsContainer,
  ChartsContainer,
  LogoContainer,
  MainTitle,
  SocChartsContainer,
  EnvChartsContainer,
  LandFillContainer,
  EmissionContainer,
  ReqContainer,
  ChartPaper,
  ChartTitle,
  colorGreen
} from "./styles";
import {
  ValueContainer,
  MapContainer,
  ControlContainer,
  FamiliesContainer
} from "./containers";
import { Message } from "./components";
import {
  fontSizeM,
  FlexContainer,
  TextSpan,
  fontWeightL,
  fontSizeXL
} from "../../styles";
import { LandFillSubContainer, ContainerChart } from "./styles/styledContainers";
import { useSelector } from "react-redux";
import {
  selectAllSelectedVolume,
  selectAllSelectedWeight
} from "../../store/furnitureBankReducer/selectors";
import { ConvertedNumber } from "../../components";
import { getArray } from "../../utils";

const AVG_MONTHLY_WASTE = 130;
const AVG_WEEKLY_WASTE = 30;
const CARGO_VOLUME = 1179

const Dashboard = () => {
  const [ monthCount, setMonthCount ] = useState(0)
  const [ weekCount, setWeekCount ] = useState(0)

  const weight = useSelector(selectAllSelectedWeight);
  const volume = useSelector(selectAllSelectedVolume);

  useEffect(() => {
    const getMonth = () => Math.floor(weight/AVG_MONTHLY_WASTE)
    if(getMonth() !== monthCount){
      setMonthCount(getMonth())
    }
  }, [monthCount, weight])  

  useEffect(() => {
    const getWeek = () => Math.floor((weight - (monthCount * AVG_MONTHLY_WASTE))/AVG_WEEKLY_WASTE)
    if(getWeek() !== weekCount){
      setWeekCount(getWeek())
    }
  }, [monthCount, weekCount, weight])  

  const monthText = monthCount > 1 ? 'months' : 'month'
  const weekText = weekCount > 1 ? 'weeks' : 'week'

  const monthArray =  getArray(monthCount)
  const weekArray =  getArray(weekCount)

  return (
    <>
      <Helmet>
        <title>Furniture Bank 2019</title>
      </Helmet>
      <MainContainer>
        <DashboardContainer>
          <ControlsContainer>
            <LogoContainer>
              <img
                src="/static/logos/furnitureBank.svg"
                alt="furniture bank icon"
              />
            </LogoContainer>
            <ControlContainer />
          </ControlsContainer>
          <ChartsContainer>
            <SocChartsContainer>
              <MainTitle gridArea="title">
                What is the estimated social impact of your donation?
              </MainTitle>
              <MapContainer />
              <FamiliesContainer />
              <ValueContainer />
            </SocChartsContainer>
            <EnvChartsContainer>
              <MainTitle gridArea="title">
                What is the estimated environmental impact of your donation?
              </MainTitle>
              <ChartPaper gridArea="landfill">
                <LandFillContainer>
                  <ChartTitle>
                    What is the estimated amount of waste saved?
                  </ChartTitle>
                  <Message />
                  {
                    !!weight &&
                      <LandFillSubContainer gridArea="weight">
                        <FlexContainer area="num">
                          <ConvertedNumber
                            data={weight}
                            size={fontSizeXL}
                            weight={fontWeightL}
                            color={colorGreen}
                            animate
                            smallSuffix
                            suffixSize={fontSizeM}
                            suffixLineHeight={1.5}
                            customSuffix="lbs"
                          />
                        </FlexContainer>
                        <FlexContainer
                          area="icon"
                          align="flex-end"
                        >
                            { 
                              monthArray && monthArray.length > 0 &&
                              monthArray.map(
                                el => 
                                <img
                                  key={el}
                                  className="garbage-month"
                                  src="/static/icons/furnitureBank/garbage.svg"
                                  alt="garbage month icon"
                                />
                              )
                            }
                            {
                              weekArray && weekArray.length > 0 && 
                              weekArray.map(
                                el => 
                                <img
                                  key={el}
                                  className="garbage-week"
                                  src="/static/icons/furnitureBank/garbage.svg"
                                  alt="garbage week icon"
                                />
                              )
                            }
                        </FlexContainer>
                        <FlexContainer area="text">
                          <TextSpan>
                            {
                              !!monthCount &&
                              `${monthCount} ${monthText}`
                            }
                            {
                              !!monthCount && !!weekCount &&
                              ` and `
                            }
                            {
                              !!weekCount ?
                              `${weekCount} ${weekText}` :
                              !monthCount && `Less than 1 ${weekText}` 
                            }
                            {' '} of waste in an avg. Canadian household
                          </TextSpan>
                        </FlexContainer>
                      </LandFillSubContainer>
                    }
                    {
                      !!volume &&
                      <LandFillSubContainer gridArea="volume">
                        <FlexContainer area="num">
                          <ConvertedNumber
                            data={volume}
                            size={fontSizeXL}
                            weight={fontWeightL}
                            color={colorGreen}
                            animate
                            smallSuffix
                            suffixSize={fontSizeM}
                            suffixLineHeight={1.5}
                            customSuffix="cu.ft"
                          />
                        </FlexContainer>
                        <FlexContainer 
                          area="icon" 
                          align="flex-start"
                        >
                          <ContainerChart
                            height={volume/CARGO_VOLUME}
                          />
                        </FlexContainer>
                        <FlexContainer area="icon">
                          <img
                            className="container"
                            src="/static/icons/furnitureBank/container.svg"
                            alt="container icon"
                          />
                        </FlexContainer>
                        <FlexContainer 
                          area="icon" 
                          align="flex-start"
                        >
                          <ContainerChart
                            height={volume/CARGO_VOLUME}
                          />
                        </FlexContainer>
                        <FlexContainer area="icon">
                          <img
                            className="container"
                            src="/static/icons/furnitureBank/container.svg"
                            alt="container icon"
                          />
                        </FlexContainer>
                        <FlexContainer area="text">
                          <TextSpan>
                              Equivalent to {' '}
                              <ConvertedNumber
                                data={volume/CARGO_VOLUME}
                                display="inline-block"
                                perc
                                animate
                              />
                              {' '} of an avg. cargo container
                          </TextSpan>
                        </FlexContainer>
                      </LandFillSubContainer>
                    }
                </LandFillContainer>
              </ChartPaper>
              <EmissionContainer>
                <ChartPaper>
                  <ChartTitle>
                    What is the estimated CO2 emission saved?
                  </ChartTitle>
                  <Message />
                </ChartPaper>
              </EmissionContainer>
            </EnvChartsContainer>
            <ReqContainer>
              <img
                className="button"
                src="/static/icons/furnitureBank/requestButton.svg"
                alt="request button icon"
              />
            </ReqContainer>
          </ChartsContainer>
        </DashboardContainer>
      </MainContainer>
    </>
  );
};
export default Dashboard;
