import React, { useState } from "react";
import Autosuggest from "react-autosuggest";
import match from "autosuggest-highlight/match";
import parse from "autosuggest-highlight/parse";
import TextField from "@material-ui/core/TextField";
import Paper from "@material-ui/core/Paper";
import MenuItem from "@material-ui/core/MenuItem";
import { fontWeightS, fontWeightL } from "../../styles/sharedStyles";
import { Grid } from "@material-ui/core";
import { useStyles, AddIcon } from "./styles";


const escapeRegexCharacters = str => str.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
const getSuggestionValue = (suggestion, key) => `${suggestion[key]}`

const getSuggestions = (data, key, value) => {
  const escapedValue = escapeRegexCharacters(value.trim());

  if (escapedValue === "") {
    return [];
  }

  const regex = new RegExp("\\b" + escapedValue, "i");
  console.log(data, key);

  return data.filter(el => regex.test(getSuggestionValue(el[key], key)));
}


const renderSuggestion = (suggestion, { query }) => {
