import React from "react";
import { Helmet } from "react-helmet";

import {
  MainContainer,
  DashboardContainer,
  ControlsContainer,
  ChartsContainer,
  LogoContainer,
  MainTitle,
  SocChartsContainer,
  EnvChartsContainer,
  EmissionContainer,
  ReqContainer,
  ChartPaper,
  ChartTitle,
  colorGreen
} from "./styles";
import {
  ValueContainer,
  MapContainer,
  ControlContainer,
  FamiliesContainer,
  LandFillContainer
} from "./containers";
import { Message } from "./components";
import { FixedInfoButton, QuestionMarkGillSemiBold } from "../../components";
import { useDispatch, useSelector } from "react-redux";
import { toggleModal } from "../../store/furnitureBankReducer/actions";
import styled, {css} from 'styled-components'
import { Paper } from "@material-ui/core";

const ModalBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, .4);
  display: ${props => props.visible ? 'block' : 'none'};
  z-index: 1000;
`

const ModalPaper = styled(Paper)`
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  width: 600px;
  height: 300px;
  z-index: 1100;
  transition: 15s opacity;

  ${props => props.visible ? css`
    opacity: 1;
    ${'' /* display: block; */}
  ` : css`
    opacity: 0;
    ${'' /* display: none; */}
  `}
`

const Dashboard = () => {

  const dispatch = useDispatch()
  const { openModal } = useSelector(state => state.furnitureBankReducer)

  const handleToggleModal = () => dispatch(toggleModal())

  return (
    <>
      <Helmet>
        <title>Furniture Bank 2019</title>
      </Helmet>
      <MainContainer>
        <DashboardContainer>
        <ModalBackground 
          onClick={handleToggleModal} 
          visible={openModal}
        />
        <ModalPaper
          visible={openModal}
        >
          Hello
        </ModalPaper>
        <FixedInfoButton
          top={0}
          right={0}
          height={120}
          width={120}
          round
          corner
          background={colorGreen}
          translate="50%, -50%"
          iconComponent={
            <QuestionMarkGillSemiBold height={25} width={25} fill="#fff" />
          }
          iconLeft="19%"
          iconBottom="17%"
          handleClick={handleToggleModal}
        />
          <ControlsContainer>
            <LogoContainer>
              <img
                src="/static/logos/furnitureBank.svg"
                alt="furniture bank icon"
              />
            </LogoContainer>
            <ControlContainer />
          </ControlsContainer>
          <ChartsContainer>
            <SocChartsContainer>
              <MainTitle gridArea="title">
                What is the estimated social impact of your donation?
              </MainTitle>
              <MapContainer />
              <FamiliesContainer />
              <ValueContainer />
            </SocChartsContainer>
            <EnvChartsContainer>
              <MainTitle gridArea="title">
                What is the estimated environmental impact of your donation?
              </MainTitle>
              <LandFillContainer />
              <EmissionContainer>
                <ChartPaper>
                  <ChartTitle>
                    What is the estimated CO2 emission saved?
                  </ChartTitle>
                  <Message />
                </ChartPaper>
              </EmissionContainer>
            </EnvChartsContainer>
            <ReqContainer>
              <a
                href="https://www.furniturebank.org/request-a-pick-up/"
                target="_blank"
                rel="noopener noreferrer"
              >
                <img
                  className="button"
                  src="/static/icons/furnitureBank/requestButton.svg"
                  alt="request button icon"
                />
              </a>
            </ReqContainer>
          </ChartsContainer>
        </DashboardContainer>
      </MainContainer>
    </>
  );
};
export default Dashboard;
