import { createBreakPoints } from '../../../styles/mediaQueries'
import {
  MainContainer,
  DashboardContainer,
  ControlsContainer,
  LogoContainer,
  ControlContainer,
  PanelContainer,
  ChartsContainer
} from './styledContainers'
import {
  MainTitle
} from './styledElements'

export const colorGreen = '#3db692'
export const colorRedDark = '#b02b2c'
export const colorRed = '#ec1c24'
export const colorGreyLight = 'lightgray'
export const colorGreyDark = '#333'

const breakPoints = {
  medium: 1024
}

export const media = createBreakPoints(breakPoints)
export { 
  MainContainer,
  DashboardContainer,
  ControlsContainer,
  LogoContainer,
  ControlContainer,
  PanelContainer,
  ChartsContainer
}
export {
  MainTitle
}
