import React from "react";
import styled from "styled-components";
import {
  paddingXS,
  fontWeightXS,
  fontSizeM,
  paddingS,
  fontWeightXL,
  fontWeightL
} from "../../styles/sharedStyles";
import { colorGreyDark } from "../../containers/FurnitureBank2019/styles";
import { useDispatch } from "react-redux/lib/hooks/useDispatch";
import { incrementItem, decrementItem } from "../../store/furnitureBankReducer/actions";

const ControlElement = styled.div`
  position: absolute;
  top: ${props => props.top}rem;
  left: ${props => props.left || 1.4}rem;
  cursor: pointer;
  font-weight: ${fontWeightL};
  visibility: hidden;
  color: #fff;
`;

const Container = styled.div`
  position: relative;

  display: flex;
  align-items: center;

  width: calc(100% - 4rem);
  height: ${props => props.height || 30}px;
  padding: ${paddingXS};
  margin: ${paddingS};
  padding-left: 3rem;
  font-weight: ${props => props.fontWeight || fontWeightXS};
  font-size: ${fontSizeM};
  border: 1px solid ${props => props.borderColor || colorGreyDark};
  border-radius: 4px;

  background: ${colorGreyDark};
  color: #fff;

  transition: 0.5s all;

  &:hover ${ControlElement}, &:hover ${ControlElement} {
    visibility: visible;
  }
`;

const QuantityContainer = styled.div`
  position: absolute;
  width: 15px;
  left: 1rem;
  top: 50%;
  transform: translateY(-50%);

  display: flex;
  justify-content: center;

  font-weight: ${fontWeightXL};
`;

const ItemChip = ({ style }) => {
  const dispatch = useDispatch();
  return (
    <Container {...style}>
      <ControlElement
        top={-0.2}
        onClick={() => dispatch(incrementItem("Air Conditioner"))}
      >
        +
      </ControlElement>
      <ControlElement
        top={2}
        left={1.5}
        onClick={() => dispatch(decrementItem("Air Conditioner"))}
      >
        -
      </ControlElement>
      <QuantityContainer>2</QuantityContainer>
      Air Conditioner
    </Container>
  );
};

export default ItemChip;
