import React from 'react';
import styled, { css } from 'styled-components'
import { paddingXS, fontWeightXS, fontSizeM, paddingS, fontWeightXL, fontSizeL, fontSizeS, fontSizeXS } from '../../styles/sharedStyles';
import { colorGreyDark } from '../../containers/FurnitureBank2019/styles';

const Container = styled.div`
    position: relative;

    display: flex;
    align-items: center;

    width: calc(100% - 4rem);
    height: ${props => props.height || 30}px;
    padding: ${paddingXS};
    margin: ${paddingS};
    padding-left: 3rem;
    font-weight: ${props => props.fontWeight || fontWeightXS};
    font-size: ${fontSizeM};
    border: 1px solid ${props => props.borderColor || colorGreyDark};
    border-radius: 4px;

    background: ${colorGreyDark};
    color: #fff;

    transition: .5s all;

    &:focus {
      outline: none;
    }
`

const QuantityContainer = styled.div`
  position: absolute;
  width: 20px;
  left: 1rem;
  top: 50%;
  transform: translateY(-50%);

  display: flex;
  justify-content: center;

  font-weight: ${fontWeightXL};

  &:after{
    content: "+";
    ${'' /* font-size: ${fontSizeS}; */}
    position: absolute;
    top: -13px;
    left: 50%;
    transform: translateX(-50%);
    cursor: pointer;
  }

  &:before{
    content: "-";
    position: absolute;
    ${'' /* font-size: ${fontSizeXS}; */}
    transform: translateX(-50%);
    top: 12px;
    left: 50%;
    cursor: pointer;
  }
`

const ItemChip = ({
  style
}) => {

  return (
    <Container
      {...style}
    >
      <QuantityContainer>
        2
      </QuantityContainer>
      Item Chip
    </Container>
  )
}

export default ItemChip
