import React, { useRef, useEffect, useState } from "react";
import styled from "styled-components";
import { useSvgResize, usePrevious } from "../../../hooks";
import { select } from "d3-selection";
import { colorGreen, colorGreyDark } from "../styles";
import { easeCubicInOut } from 'd3-ease'
import 'd3-transition'
import { createUpdateSvg } from "../../../utils";

const ChartWrapper = styled.div`
  position: relative;
  height: 70%;
  width: 90%;

  border: 1px solid ${colorGreyDark};

  border-radius: 20px;
  overflow: hidden;

  svg {
    position: absolute;

    rect {
      fill: ${colorGreen};
    }
  }
`;

const PercentageChart = ({ 
  data,
  fullCount,
  updateDuration=1000
}) => {
  // const svgRef = useRef();
  const divRef = useRef();
  const storedValues = useRef();
  const prevData = usePrevious(data);
  const prevCount = usePrevious(fullCount);
  const [init, setInit] = useState(false);
  let initVis, updateData

  const dims = useSvgResize(divRef);

  useEffect(() => {
    if (dims.width && dims.height && data && !init) {
      initVis();
      setInit(true);
    }
  }, [data, dims.height, dims.width, init, initVis]);

  useEffect(() => {
    //console.log('dims', dims)
  }, [dims]);

  useEffect(() => {
    if(init && (prevData !== data)){
      if(prevCount !== fullCount){
        resetChart()
        updateData()
      } else {
        updateData()
      }
    }
  }, [data, init, prevData, updateData, fullCount, prevCount]);

  initVis = () => {
    const area = select(divRef.current)
    const { chartArea } = createUpdateSvg({
      area,
      dims: {...dims, height: dims.height},
      append: true
    })

    chartArea
      .append('rect')
      .attr('x', 0)
      .attr('y', 0)
      .attr('height', dims.height)
      .attr('width', 0)

    storedValues.current = {
      area,
      chartArea
    };

    updateRect(updateDuration)

  };

  updateData = () => {
    updateRect(updateDuration)
  };

  const resetChart = () => {
    const { chartArea } = storedValues.current
    chartArea.select('rect')
      .transition()
      .duration(500)
      .ease(easeCubicInOut)
      .attr('width', 1)
      .transition()
      .duration(0)
      .attr('width', 0)
  };

  const updateDims = () => {};

  const updateRect = duration => {
    const { chartArea } = storedValues.current

    chartArea.select('rect')
      .transition()
      .duration(duration)
      .ease(easeCubicInOut)
      .attr('width', dims.width * (data - fullCount))
  }

  return <ChartWrapper ref={divRef}/>
};

export default PercentageChart;
