import React from 'react';
import styled, { css } from 'styled-components'
import { paddingXS, fontWeightXS, fontSizeM, paddingS, fontWeightXL, fontSizeL } from '../../styles/sharedStyles';
import { colorGreyDark } from '../../containers/FurnitureBank2019/styles';

const Container = styled.div`
    position: relative;

    display: flex;
    align-items: center;

    width: calc(100% - 4rem);
    height: ${props => props.height || 30}px;
    padding: ${paddingXS};
    margin: ${paddingS};
    padding-left: 3rem;
    font-weight: ${props => props.fontWeight || fontWeightXS};
    font-size: ${fontSizeM};
    border: 1px solid ${props => props.borderColor || colorGreyDark};
    border-radius: 4px;

    background: ${colorGreyDark};
    color: #fff;

    transition: .5s all;

    &:focus {
      outline: none;
    }
`

const QuantityContainer = styled.div`
  position: absolute;
  left: 1.2rem;
  top: 50%;
  transform: translateY(-50%);

  font-weight: ${fontWeightXL};
`

const ItemChip = ({
  style
}) => {

  return (
    <Container
      {...style}
    >
      <QuantityContainer>
        2
      </QuantityContainer>
      Item Chip
    </Container>
  )
}

export default ItemChip
