import React, { useRef, useEffect } from 'react';
import styled from 'styled-components'
import { useSvgResize } from '../../../hooks';
import { testBorder } from '../../../styles';

const ChartWrapper = styled.div`
  position: relative;
  ${testBorder};
  height: 100%;
  width: 100%;

  svg {
    position: absolute;
    width: 100%;
    height: 100%;
  }
`

const PercentageChart = ({
  data
}) => {
  const svgRef = useRef() 

  const dims = useSvgResize(svgRef)

  useEffect(() => {
    console.log('dims', dims)
  }, [dims])


  useEffect(() => {
    console.log('data', data)
  }, [data])



  return (
    <ChartWrapper>
      <svg
        ref={svgRef}
      />
    </ChartWrapper>
  )
}

export default PercentageChart
